class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        m_h, n_h = m // 2, n // 2
        res = 0
        f = False
        c = 0
        if m % 2 == 1:
            if n % 2 == 1:
                res += grid[m_h][n_h]
            for j in range(n_h):
                if grid[m_h][j] != grid[m_h][n-1-j]:
                    f = True
                    res += 1
                else:
                    c += grid[m_h][j] * 2
        for i in range(m_h):
            i2 = m - 1 - i
            for j in range(n_h):
                j2 = n - 1 - j
                s = grid[i][j] + grid[i][j2] + grid[i2][j] + grid[i2][j2]
                res += min(s, 4 - s)
            if n % 2 == 1:
                if grid[i][n_h] != grid[i2][n_h]:
                    f = True
                    res += 1
                else:
                    c += grid[i][n_h] * 2
        if c % 4 == 2 and not f:
            res += 2
        return res
