class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        for(int i=0; i<(grid.size())/2; i++){
            for(int j=0; j<(grid[0].size())/2; j++){
                int f=0;
                if(grid[i][j])  f++;
                if(grid[i][grid[0].size()-1-j]) f++;
                if(grid[grid.size()-1-i][j])    f++;
                if(grid[grid.size()-1-i][grid[0].size()-1-j])   f++;
                ans+=min(f,4-f);
            }
        }
        int m=0,mm=0;
        if(grid.size()%2){
            for(int i=0; i<grid[0].size()/2; i++){
                if(grid[grid.size()/2][i]!=grid[grid.size()/2][grid[0].size()-1-i]){
                    m++;
                }
                else if(grid[grid.size()/2][i]){
                    mm+=2;
                }
            }
        }
        if(grid[0].size()%2){
            for(int i=0; i<grid.size()/2; i++){
                if(grid[i][grid[0].size()/2]!=grid[grid.size()-1-i][grid[0].size()/2]){
                    m++;
                }
                else if(grid[i][grid[0].size()/2]){
                    mm+=2;
                }
            }
        }
        if(grid.size()%2 && grid[0].size()%2 && grid[grid.size()/2][grid[0].size()/2]){
            ans++;
        }
        ans+=m;
        if(mm%4){
            if(m==0){
                ans+=2;
            }
        }
        return ans;
    }
};
