    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<int>hor(n + 1, 0), ver(m + 1, 0);
        int cnt = 0;
        for(int i = 0; i < n / 2; i++) {
            for(int j = 0; j < m / 2; j++) {
                int c0 = 0, c1 = 0;
                if(grid[i][j] == 0) c0++; 
                else c1++;
                if(grid[i][m - 1 - j] == 0) c0++;
                else c1++;
                if(grid[n - 1 - i][j] == 0) c0++;
                else c1++;
                if(grid[n - 1 - i][m - 1 - j] == 0) c0++;
                else c1++;
                if(c0 > c1) cnt += c1;
                else cnt += c0;
            }
        }
        int c1 = 0;
        int h = 0, v = 0;
        if(m % 2) {
            // for(int i = 0; i < n; i++) {
            //     c1 += grid[i][m / 2];
            // }
            for(int i = 0; i < n / 2; i++) {
                if(grid[i][m / 2] != grid[n - 1 - i][m / 2]) v++;
                else {
                    if(grid[i][m / 2] == 1) c1 += 2;
                }
            }
        }
        if(n % 2) {
            // for(int j = 0; j < m; j++) {
            //     c1 += grid[n / 2][j];
            // }
            for(int j = 0; j < m / 2; j++) {
                if(grid[n / 2][m - 1 - j] != grid[n / 2][j]) h++;
                else {
                    if(grid[n / 2][m - 1 - j] == 1) c1 += 2;
                }
            }
        }
        if(n % 2 && m % 2 && grid[n / 2][m / 2] == 1) cnt++;
        if(c1 % 4 == 2) {
            if(h + v > 0) 
                return cnt + h + v;
            else 
                return cnt + 2;
        }
