class Solution {
public:
    int ans=0,f=0,ct=0;
    void solve(int n,int m ,vector<vector<int>>& v)
    {
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int i1=n-1-i,j1=j,i2=i,j2=m-1-j,i3=n-1-i,j3=m-1-j;
                int c=v[i][j]+v[i1][j1]+v[i2][j2]+v[i3][j3];
                ans+=min(c,4-c);
            }
        }
    }
    void solver(int n,int m ,vector<vector<int>>& v)
    {
        int i=n/2 ;
        for(int j=0;j<m/2;j++)
        {
            if(v[i][j]!=v[i][m-1-j]) {ans++; f=1;}
            if(v[i][j]==1&&v[i][m-1-j]==1) ct++;
        }
    }
    void solvec(int n,int m ,vector<vector<int>>& v)
    {
        int j=m/2;
        for(int i=0;i<n/2;i++)
        {
            if(v[i][j]!=v[n-1-i][j]){ ans++;f=1;}
            if(v[i][j]==1&&v[n-1-i][j]==1) ct++;
        }
    }
    int minFlips(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size();
        solve(n,m,v);
        if(n%2!=0) solver(n,m,v);
        if(m%2!=0) solvec(n,m,v);
        if(n%2!=0&&m%2!=0&&v[n/2][m/2]==1) ans++;
        if(ct%2==1&&f==0) ans+=2;
        return ans;
    }
};
