class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int count1=0;
        int ans = 0;
        for(int i = 0; i < m/2; ++i) {
            for(int j = 0; j < n/2; ++ j) {
                int _i = m-1-i,_j=n-1-j;
                int count = grid[i][j] + grid[i][_j]+grid[_i][j]+grid[_i][_j];
                ans += Math.min(count, 4-count);
            }
        }
        int a = 0,b=0;
        if(m%2==1) {
            for(int j = 0; j < n/2;++j) {
                if(grid[m/2][j]!= grid[m/2][n-1-j]) {
                    ans++;
                    a++;
                }
                else {
                    count1 += grid[m / 2][j] * 2;
                    b++;
                }
            }
        }
        if(n%2==1) {
            for(int i = 0; i < m/2;++i) {
                if(grid[i][n/2] != grid[m - 1 - i][n/2]) {
                    ans++;
                    ++a;
                }
                else {
                    count1 += grid[i][n/2]*2;
                    b++;
                }
            }
        }
        if(m%2==1&&n%2==1) if(grid[m/2][n/2]==1) ans ++;
        if(count1%4!=0) {
            if(a == 0) {
                ans += 2;
            }

        }
        return ans;

    }
}
