class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int ans = 0;
        int c = 0;
        int qc = 0;
        for (int i = 0; i < (m + 1)/2; i++) {
            for (int j = 0; j < (n+1)/2; j++) {
                int ones = 0, zeros = 0;
                ones += grid[i][j];
                ones += grid[m - 1 - i][j];
                ones += grid[i][n - 1 - j];
                ones += grid[m-1-i][n-1-j];
                zeros += 1 - grid[i][j];
                zeros += 1 - grid[m - 1 - i][j];
                zeros += 1 - grid[i][n - 1 - j];
                zeros += 1 - grid[m-1-i][n-1-j];

                if (i == m - 1 - i && j != n - 1 - j) {
                    ans += (ones == zeros ? 1 : 0);
                    if (ones == 4) {
                        c += 1;
                    } else if (ones == zeros) {
                        // c += 1;
                        qc += 1;
                    }
                } else if (i != m - 1 - i && j == n - 1 - j) {
                    ans += (ones == zeros ? 1 : 0);
                    if (ones == 4) {
                        c += 1;
                    } else if (ones == zeros) {
                        // c += 1;
                        qc += 1;
                    }

                } else if (i == m - 1 - i && j == n - 1 - j) {
                    if (grid[i][j] == 1) {
                        ans++;
                    }
                } else {
                    if (ones == 1 || ones == 3) {
                        ans += 1;
                    } else if (ones == 2) {
                        ans += 2;
                    }
                }
            }
        }

        if (c % 2 == 1 && qc == 0) {
            ans += 2;
        }

        return ans;
    }
}
