class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;
        int score = 0;
        int score2 = 0;
        int ans2 = 0;
        for(int i = 0 ; i * 2 < n ; i++) {
            for(int j = 0 ; j * 2 < m ; j++) {
                if(i == n - 1 - i && j == m - 1 - j) {
                    ans += grid[i][j];
                } else if (i == n - 1 - i) {
                    int curr = grid[i][j] + grid[i][m - 1 - j];
                    if(curr == 1) {
                        score2 += 2;
                        ans2 += 1;
                    } else if(curr == 2) {
                        score2 += 2;
                    }
                } else if (j == m - 1 - j) {
                    int curr = grid[i][j] + grid[n - 1 - i][j];
                    if(curr == 1) {
                        score2 += 2;
                        ans2 += 1;
                    } else if(curr == 2) {
                        score2 += 2;
                    }
                } else {
                    int curr = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];
                    if(2 * curr > 4) {
                        score += 4;
                        ans += 4 - curr;
                    } else ans += curr;
                }
            }
        }
        if((score + score2) % 4 != 0) {
            if(ans2 == 0) ans2 += 2;
        }
        return ans + ans2;
    }
}
