class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                cand = [grid[i][j], grid[i][n - 1- j], grid[m - 1 - i][j], grid[m - 1 - i][n - 1- j]]
                cnt = Counter(cand)
                if len(cnt) == 1: continue
                ans += min(cnt.values())
        cur = 0
        if m % 2 and n % 2:
            cur = grid[m // 2][n // 2]
            x, one, zero = 0, 0, 0
            for j in range(n // 2):
                if grid[m // 2][j] != grid[m // 2][n - 1 - j]:
                    x += 1
                elif grid[m // 2][j]:
                    one += 1
                else:
                    zero += 1
            for i in range(m // 2):
                if grid[i][n // 2] != grid[m - 1 - i][n // 2]:
                    x += 1
                elif grid[i][n // 2]:
                    one += 1
                else:
                    zero += 1
            if not one % 2:
                cur += x
            else:
                if x > 0:
                    cur += x
                else:
                    cur += 2
        elif m % 2:
            x, one, zero = 0, 0, 0
            for j in range(n // 2):
                if grid[m // 2][j] != grid[m // 2][n - 1 - j]:
                    x += 1
                elif grid[m // 2][j]:
                    one += 1
                else:
                    zero += 1
            if not one % 2:
                cur += x
            else:
                if x > 0:
                    cur += x
                else:
                    cur += 2
        elif n % 2:
            x, one, zero = 0, 0, 0
            for i in range(m // 2):
                if grid[i][n // 2] != grid[m - 1 - i][n // 2]:
                    x += 1
                elif grid[i][n // 2]:
                    one += 1
                else:
                    zero += 1
            if not one % 2:
                cur += x
            else:
                if x > 0:
                    cur += x
                else:
                    cur += 2
        ans += cur
        return ans







