class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int mid_hang = n / 2;
        int mid_lie = m / 2;
        int cnt = 0;
        for (int i = 0; i < mid_hang; ++i)
        {
            for (int j = 0; j < mid_lie; ++j)
            {
                int tmp = 0;
                if (grid[i][j] == 0)++tmp;
                if (grid[n - i - 1][j] == 0)++tmp;
                if (grid[i][m - j - 1] == 0)++tmp;
                if (grid[n - i - 1][m - j - 1] == 0)++tmp;
                cnt += min(tmp, 4 - tmp);
            }
        }
        int sp = 0;
        int num_1 = 0;
        if (m % 2)
        {
            for (int i = 0; i < mid_hang; ++i)
            {
                if (grid[i][mid_lie] == 1)
                    ++num_1;
                if (grid[n - i - 1][mid_lie] == 1)
                    ++num_1;
                if (grid[i][mid_lie] != grid[n - i - 1][mid_lie])
                    ++sp;
            }
        }

        if (n % 2)
        {
            for (int j = 0; j < mid_lie; ++j)
            {
                if (grid[mid_hang][j] == 1)
                    ++num_1;
                if (grid[mid_hang][m - j - 1] == 1)
                    ++num_1;
                if (grid[mid_hang][j] != grid[mid_hang][m - j - 1])
                    ++sp;
            }
        }
        cnt += sp;
        if (m % 2 && n % 2 && grid[mid_hang][mid_lie] == 1)
            cnt++;
        if (sp == 0 && num_1 % 4 != 0)
            cnt += 2;
        return cnt;
    }
};
