class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n, res = len(grid), len(grid[0]), 0

        for i in range(m//2):
            for j in range(n//2):
                s = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j]
                res += min(s, 4-s)

        if (m&1) and (n&1) and grid[m//2][n//2]:
            res += 1

        same, diff = 0, 0
        if m & 1:
            x, y = 0, n-1
            while x < y:
                same += grid[m//2][x] & grid[m//2][y]
                diff += grid[m//2][x] ^ grid[m//2][y]
                x += 1
                y -= 1

        if n & 1:
            x, y = 0, m-1
            while x < y:
                same += grid[x][n//2] & grid[y][n//2]
                diff += grid[x][n//2] ^ grid[y][n//2]
                x += 1
                y -= 1

        res += diff

        if diff == 0 and (same & 1):
            res += 2

        return res
