class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int sum00=0,sum01=0,sum11=0,sum1=0;
        int n=grid.size();
        int m=grid[0].size();
        for(int i=0;i<grid.size();i++){
            int t1=i,t2=n-i-1;
            for(int j=0;j<m;j++){
                int t3=j,t4=m-j-1;
                if(t1==t2&&t3==t4){
                    if(grid[i][j]==1)sum1=1;
                }
                else if(t1==t2){
                    int op=grid[t1][t3]+grid[t1][t4];
                    if(op==0)sum00++;
                    if(op==1)sum01++;
                    if(op==2)sum11++;
                }
                else if(t3==t4){
                    int op=grid[t1][t3]+grid[t2][t3];
                    if(op==0)sum00++;
                    if(op==1)sum01++;
                    if(op==2)sum11++;
                }
                else{
                    int op=grid[t1][t3]+grid[t1][t4]+grid[t2][t3]+grid[t2][t4];
                    ans+=min(op,4-op);
                }
            }
        }
        ans/=4;
        sum00/=2;
        sum01/=2;
        sum11/=2;
        sum11%=2;
        ans+=sum01;
        if(!sum01&&sum11%2)ans+=2;
        return ans+sum1;
    }
};
