class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        //(i, j) 和 (n - i - 1, j) 和 (i, m - j - 1) 和 (n - i - 1, m - j - 1)
        //是绑定的

        //要把两个一组的特殊考虑 奇数的中间
        using PII = std::pair<int, int>;
        int res = 0, group2 = 0, group1 = 0, peidui1 = 0;
        for(int i = 0; i < (n - 1)/2 + 1; i++) {
            for(int j = 0; j < (m - 1)/2 + 1; j++) {
                std::set<PII> alls;
                alls.insert({i, j});
                alls.insert({n - i - 1, j});
                alls.insert({i, m - j - 1});
                alls.insert({n - i - 1, m - j - 1});

                int cnt0 = 0, cnt1 = 0, count = alls.size();
                for(auto& [x, y] : alls) {
                    if(grid[x][y]) cnt1++;
                    else cnt0++;
                }

                if(count == 4) res += std::min(cnt0, cnt1);
                else if(count == 2) {
                    if(cnt1 == 2) peidui1 += 2;
                    else if(cnt1 == cnt0) group2++;
                }
                else group1++;
            }
        }
        //若中间为1要翻
        if(group1 && grid[n/2][m/2]) res++;
        if(peidui1%4 == 0) res += group2;
        else {
            if(group2) res += group2; 
            else res += 2;
        }
        return res; 
    }
};
