class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();


        if(m*n <= 2){
            int ans = 0;
            for(int i = 0; i<m; i++){
                for(int j = 0; j<n; j++){
                    if(grid[i][j] == 1){
                        ans++;
                    }
                }
            }

            return ans;
        }

        int ans = 0;
        int x = m/2;
        int y = n/2;
        for(int i = 0; i<x; i++){
            for(int j = 0; j<y; j++){
                int ones = grid[i][j] + grid[m-i-1][j] + grid[i][n-j-1] + grid[m-i-1][n-j-1];
                ans += min(4-ones, ones);
            }
        }

        int req = 0;
        int ones = 0;
        if(m % 2 == 1){
            int l = 0;
            int r = n-1;

            while(l < r){
                if(grid[x][l] != grid[x][r]){
                    req++;
                }else{
                    if(grid[x][l] == 1){
                        ones+=2;
                    }
                }

                l++;
                r--;
            }
        }

        if(n % 2 == 1){
            int l = 0;
            int r = m-1;

            while(l < r){
                if(grid[l][y] != grid[r][y]){
                    req++;
                }else{
                    if(grid[l][y] == 1){
                        ones+=2;
                    }
                }

                l++;
                r--;
            }
        }

        if(m%2 == 1 && n%2 == 1){
            if(grid[x][y] == 1){
                ans++;
            }
        }

        if(req > 0){
            ans += req;
        }else if(ones%4 != 0){
             ans += 2;
        }

        return ans;
    }
};
