class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0, m = grid.size(), n = grid[0].size();

        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int c = grid[i][j] + grid[m-i-1][j] + grid[i][n-j-1] + grid[m-i-1][n-j-1];

                if(c>2)ans+=(4-c);
                else{
                    ans+=c;
                }
            }
        }

        if(m%2 && n%2 && grid[m/2][n/2] == 1)ans++;
        int c = 0, o = 0;
        if(m%2){
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j] != grid[m/2][n-j-1])c++;
                if(grid[m/2][j] == grid[m/2][n-j-1] && grid[m/2][j] == 1)o++;
            }
        }
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2] != grid[m-i-1][n/2])c++;
                if(grid[i][n/2] == grid[m-i-1][n/2] && grid[i][n/2] == 1)o++;
            }
        }
        o %= 2;
        if(o && c == 0){
            ans += 2; 
        }else ans+= c;
        return ans;
    }
};
