class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        N = len(grid)
        M = len(grid[0])

        c = 0

        for i in range(N // 2):
            for j in range(M // 2):
                p1 = grid[i][j]
                p2 = grid[i][-(j + 1)]
                p3 = grid[-(i + 1)][j]
                p4 = grid[-(i + 1)][-(j + 1)]

                s = p1 + p2 + p3 + p4
                c += min(s, 4 - s)

        x = 0
        f = 0

        if N % 2:
            for j in range(M // 2):
                p1 = grid[N // 2][j]
                p2 = grid[N // 2][-(j + 1)]

                s = p1 + p2
                c += min(s, 2 - s)

                if s == 2:
                    x += 2

                if s == 1:
                    f += 1

        if M % 2:
            for i in range(N // 2):
                p1 = grid[i][M // 2]
                p2 = grid[-(i + 1)][M // 2]

                s = p1 + p2
                c += min(s, 2 - s)

                if s == 2:
                    x += 2

                if s == 1:
                    f += 1

        if N % 2 and M % 2:
            c += grid[N // 2][M // 2]

        # print(f, c)
        if x % 4 and not f:
            c += 2

        return c
