class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int ans=0;
        //四个
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int x=grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-1-i][n-1-j];
                ans+=min(x,4-x);
            }
        }
        //1个
        if((m&1)&&(n&1))
        {
            if(grid[m/2][n/2])
                ans++;
            int x=0,dif=0;
            for(int i=0;i<n/2;i++)
            {
                x+=grid[m/2][i]+grid[m/2][n-i-1];
                dif+=(grid[m/2][i]!=grid[m/2][n-i-1]);
            }
            for(int i=0;i<m/2;i++)
            {
                x+=grid[i][n/2]+grid[m-1-i][n/2];
                dif+=(grid[i][n/2]!=grid[m-1-i][n/2]);
            }
            if(min(x%4,4-x%4)<=dif) ans+=dif;
            else ans+=2;
        }

        //2个
        else if(m&1)
        {
            int x=0,dif=0;
            for(int i=0;i<n/2;i++)
            {
                x+=grid[m/2][i]+grid[m/2][n-i-1];
                dif+=(grid[m/2][i]!=grid[m/2][n-i-1]);
            }
            //cout<<x<<" "<<dif<<endl;
            if(min(x%4,4-x%4)<=dif) ans+=dif;
            else ans+=2;
        }
        else if(n&1)
        {
            int x=0,dif=0;
            for(int i=0;i<m/2;i++)
            {
                x+=grid[i][n/2]+grid[m-1-i][n/2];
                dif+=(grid[i][n/2]!=grid[m-1-i][n/2]);
            }
            if(min(x%4,4-x%4)<=dif) ans+=dif;
            else ans+=2;
        }
        return ans;
    }
};
