class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size(),m=grid[0].size();
        vector<vector<int>>v;
        for(int i=0;i<(n)/2;i++){
            for(int j=0;j<(m)/2;j++){
               vector<int>c(2,0);
                c[grid[i][j]]++;
                c[grid[i][m-1-j]]++;
                c[grid[n-1-i][j]]++;
                c[grid[n-1-i][m-1-j]]++;
                ans+=min(c[1],c[0]);
            }
        }
        if((n%2)&&(m%2)){
              int cur=0,count=0,z=0;

                for(int i=0;i<n/2;i++){
                    cur+=(grid[i][(m)/2]!=grid[n-1-i][(m)/2]);
                    if(grid[i][m/2]==grid[n-1-i][m/2]) if(grid[i][m/2]) z+=2;
                }

                for(int j=0;j<m/2;j++){
                    cur+=(grid[(n)/2][j]!=grid[(n)/2][m-1-j]);
                    if(grid[n/2][j]==grid[n/2][m-1-j]) if(grid[n/2][j]) z+=2;
                }

                if(z%4){
                    if(cur==0){
                        cur+=2;
                    }   
                }
            if(grid[(n)/2][(m)/2]==1) ans++;
                ans+=cur;
        }else if((n%2)||(m%2)){
                int cur=0,count=0,z=0;
            if(m%2){
                for(int i=0;i<n/2;i++){
                    cur+=(grid[i][(m)/2]!=grid[n-1-i][(m)/2]);
                    if(grid[i][m/2]==grid[n-1-i][m/2]) if(grid[i][m/2]) z+=2;
                }
            }else{
                for(int j=0;j<m/2;j++){
                    cur+=(grid[(n)/2][j]!=grid[(n)/2][m-1-j]);
                    if(grid[n/2][j]==grid[n/2][m-1-j]) if(grid[n/2][j]) z+=2;
                }
            }
                if(z%4){
                    if(cur==0){
                        cur+=2;
                    }   
                }
                ans+=cur;
        }
        return ans;


    }
};
