class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int count =0,n=grid.size(),m=grid[0].size(),p1=0,p2=0;
        vector<vector<int>> vis(n,vector<int>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1&&vis[i][j]==0){
                    int c=0;
                    vis[i][j]=1;
                    vis[i][m-j-1]=1;
                    vis[n-i-1][m-j-1]=1;
                    vis[n-i-1][j]=1;
                    if(i!=n-i-1&&j!=m-j-1){
                        if(grid[i][j])c++;
                        if(grid[i][m-j-1])c++;
                        if(grid[n-i-1][m-j-1])c++;
                        if(grid[n-i-1][j])c++;
                        count+=min(c,4-c);
                    }else if(i==n-i-1&&j!=m-j-1){
                        if(grid[i][m-j-1])c++;
                        if(grid[i][j])c++;
                        if(c==1)p1++;
                        if(c==2)p2++;
                    }else if(i!=n-i-1&&j==m-j-1){
                        if(grid[n-i-1][j])c++;
                        if(grid[i][j])c++;
                        if(c==1)p1++;
                        if(c==2)p2++;
                    }else{
                        count++;
                    }
                }
            }
        }
        count+=p1;
        if(p2%2==1){
            if(p1==0)count+=2;
        }
        return count;
    }
};
