class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int r = grid.size(), c = grid[0].size();
        int ans = 0;
        for(int i=0;i<(r/2);i++) {
            int count1 = 0;
            for(int j=0;j<(c/2);j++) {
                count1 = (grid[i][j] + grid[i][c-j-1] + grid[r-i-1][j] + grid[r-i-1][c-j-1]);
                ans += min(4-count1, count1);
            }
        }
        int flip = 0;
        int cc = 0;
        if(r%2 == 1) {
            for(int j=0;j<(c/2);j++) {
                if(grid[(r/2)][j] != grid[(r/2)][c-j-1])
                    flip += 1;
            }
            for(int j=0;j<c;j++)
                cc += grid[(r/2)][j];
        }
        if(c%2 == 1) {
            for(int j=0;j<(r/2);j++) {
                if(grid[j][(c/2)] != grid[r-j-1][(c/2)])
                    flip += 1;
            }
            for(int j=0;j<r;j++)
                cc += grid[j][(c/2)];
        }
        int t= 0;
        if(r%2 == 1 && c%2 == 1) {
            if(grid[(r/2)][(c/2)] == 1) {
                flip++;
                cc -= 2;
                t++;
            }
        }
        if(cc%4 == 2 && (flip - t) < 2) {
            flip += (2 - (flip - t));
        }
        return (ans + (flip));
    }
};
