public class Solution {
    private int maple = 0;
    private int oak = 0;
    private int pine = 0;

    private void analyzeQuadrants(int a, int b, int[][] forest) {
        for (int x = 0; x < a / 2; x++) {
            for (int y = 0; y < b / 2; y++) {
                int x1 = a - 1 - x;
                int y1 = y;
                int x2 = x;
                int y2 = b - 1 - y;
                int x3 = a - 1 - x;
                int y3 = b - 1 - y;
                int trees = forest[x][y] + forest[x1][y1] + forest[x2][y2] + forest[x3][y3];
                maple += Math.min(trees, 4 - trees);
            }
        }
    }

    private void handleOddRow(int a, int b, int[][] forest) {
        int midRow = a / 2;
        for (int y = 0; y < b / 2; y++) {
            if (forest[midRow][y] != forest[midRow][b - 1 - y]) {
                maple++;
                oak = 1;
            }
            if (forest[midRow][y] == 1 && forest[midRow][b - 1 - y] == 1) {
                pine++;
            }
        }
    }

    private void handleOddColumn(int a, int b, int[][] forest) {
        int midCol = b / 2;
        for (int x = 0; x < a / 2; x++) {
            if (forest[x][midCol] != forest[a - 1 - x][midCol]) {
                maple++;
                oak = 1;
            }
            if (forest[x][midCol] == 1 && forest[a - 1 - x][midCol] == 1) {
                pine++;
            }
        }
    }

    public int minFlips(int[][] forest) {
        int a = forest.length;
        int b = forest[0].length;

        analyzeQuadrants(a, b, forest);

        if (a % 2 != 0) {
            handleOddRow(a, b, forest);
        }
        if (b % 2 != 0) {
            handleOddColumn(a, b, forest);
        }
        if (a % 2 != 0 && b % 2 != 0 && forest[a / 2][b / 2] == 1) {
            maple++;
        }
        if (pine % 2 == 1 && oak == 0) {
            maple += 2;
        }

        return maple;
    }


}
