class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int m = g.size();
        int n = g[0].size();
        vector<pair<int,int>>v;
        for(int i=0; i<(m+1)/2; i++){
            for(int j=0; j<(n+1)/2; j++){
                int zero=0,one=0;
                if(g[i][j]==1) one++;
                else zero++;
                if(i!=m-1-i){
                    if(g[m-1-i][j]==1) one++;
                    else zero++;
                    if(j!=n-1-j){
                        if(g[m-1-i][n-1-j]==1) one++;
                        else zero++;
                    }
                }
                if(j!=n-1-j){
                    if(g[i][n-1-j]==1) one++;
                    else zero++;
                }
                v.push_back({zero,one});
            }
        }
        int zero=0,one=0,c=0,ans=0;
        for(int i=0; i<v.size(); i++){
            int mini = min(v[i].first,v[i].second);
            if(v[i].first+v[i].second==4) ans+=mini;
            else if(v[i].first+v[i].second==1) ans+=v[i].second;
            else{
                if(v[i].first==v[i].second) c++;
                else{
                    zero+=v[i].first;
                    one+=v[i].second;
                }                
            }
        }
        if(one%4==0) ans+=c;
        else{
            int x = one%4;
            if(2*c>=x) ans+=c;
            else ans+=(c+x);
        }
        return ans;

    }
};
