class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int ans = 0;
        int n = v.size();
        int m = v[0].size();
        for (int i = 0; i < n/2; i++)
        {
            for (int j = 0; j<m/2; j++)
            {
                int one = v[i][j] + v[i][m-j-1] + v[n-i-1][m-j-1] + v[n-i-1][j];
                int zero = 4 - one;
                ans += min(one,zero);
            }
        }
        int one = 0;
        int tp = 0;
        if (n % 2)
        {
            for (int j = 0; j<m/2; j++){
                ans += (v[n/2][j] != v[n/2][m-j-1]);
                tp += (v[n/2][j] != v[n/2][m-j-1]);
                if (v[n/2][j] == v[n/2][m-j-1] && v[n/2][j])
                    one++;
            }
        }
        if (m % 2)
        {
            for (int i = 0; i<n/2; i++){
                ans += (v[i][m/2] != v[n-i-1][m/2]);
                tp += (v[i][m/2] != v[n-i-1][m/2]);
                if (v[i][m/2] == v[n-i-1][m/2] && v[i][m/2])
                    one++;
            }
        }
        if (n%2 && m%2 && v[n/2][m/2])
            ans++;
        if (one % 2 && !tp)
            ans+=2;
        return ans;
    }
};
