class Solution {
public:
    int check(string s){
        int n=s.length();
        int ans=0;
        for(int i=0; i<n/2; i++){
            if(s[i]!=s[n-i-1]) ans++;
        }
        return ans;
    }
    int minFlips(vector<vector<int>>& grid) {
        int row=0, col=0;
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int sum=grid[i][j]+grid[n-i-1][j]+grid[i][m-j-1]+grid[n-i-1][m-j-1];
                ans+=min(sum,4-sum);
            }
        }
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1) ans++;
            int row1=0, row2=0, row3=0, col1=0, col2=0, col3=0;
            for(int i=0; i<n/2; i++) {
                int sum=grid[i][m/2]+grid[n-i-1][m/2];
                if(sum==0) row1++;
                else if(sum==2) row2++;
                else{
                    ans++;
                    row3++;
                }
            }
            for(int i=0; i<m/2; i++) {
                int sum=grid[n/2][i]+grid[n/2][m-i-1];
                if(sum==0) col1++;
                else if(sum==2) col2++;
                else{
                    ans++;
                    col3++;
                }
            }
            int dl=row2*2+col2*2;
            if(dl%4==0) return ans;
            else if(dl%4==2){
                if(row3 || col3) return ans;
                else return (ans+2);
            }
        }
        else if(m%2==1){
            int row1=0, row2=0, row3=0;
            for(int i=0; i<n/2; i++) {
                int sum=grid[i][m/2]+grid[n-i-1][m/2];
                if(sum==0) row1++;
                else if(sum==2) row2++;
                else{
                    ans++;
                    row3++;
                }
            }
            int dl=row2*2;
            if(dl%4==0) return ans;
            else if(dl%4==2){
                if(row3) return ans;
                else return (ans+2);
            }
        }
        else if(n%2==1){
            int row1=0, row2=0, row3=0;
            for(int i=0; i<m/2; i++) {
                int sum=grid[n/2][i]+grid[n/2][m-i-1];
                if(sum==0) row1++;
                else if(sum==2) row2++;
                else{
                    ans++;
                    row3++;
                }
            }
            int dl=row2*2;
            if(dl%4==0) return ans;
            else if(dl%4==2){
                if(row3) return ans;
                else return (ans+2);
            }
        }
        else return ans;
        return 1;
    }
};
