class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int res = 0, res2 = 0, res3 = 0;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(i == n - 1 - i && j == m - 1 - j) {
                    res3 += (grid[i][j] == 1);
                    continue;
                }
                if(i == n - 1 - i || j == m - 1 - j) {
                    continue;
                }
                set<vector<int>> s;
                s.insert({i, j});
                s.insert({i,m - 1 - j});
                s.insert({n - 1 - i,j});
                s.insert({n - 1 - i,m - 1 - j});
                vector<int> cnt(2);
                for(auto &x : s) {
                    cnt[grid[x[0]][x[1]]]++;
                }
                int val = min(s.size() - cnt[0], s.size() - cnt[1]);
                res += val;
            }
        }
        res /= 4;
        res += res3;
        int eq = 0, uneq = 0;
        if(n % 2) {
            int i = n / 2;
            for(int j = 0; j < m; j++) {
                if(j == m - j - 1) {
                    continue;
                }
                if(grid[i][j] == grid[i][m - j - 1]) {
                    if(grid[i][j] == 1) {
                        eq++;
                    }
                }
                else {
                    uneq++;
                }
            }
        }
        if(m % 2) {
            int j = m / 2;
            for(int i = 0; i < n; i++) {
                if(i == n - i - 1) {
                    continue;
                }
                if(grid[i][j] == grid[n - 1 - i][j]) {
                    if(grid[i][j] == 1) {
                        eq++;
                    }
                }
                else {
                    uneq++;
                }
            }
        }
        eq /= 2;
        uneq /= 2;
        if(eq % 2 == 0) {
            res += uneq;
        }
        else if(eq % 2) {
            if(uneq > 0) {
                res += uneq;
            } else {
                res += 2;
            }
        }
        return res;
    }
};
