class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size();
        int m = grid[0].size();

        vector<vector<int>> vt(n + 5, vector<int>(m + 5));
        for(int i = 1; i <= n; ++i)
        {
            for(int j = 1; j <= m; ++j) vt[i][j] = grid[i - 1][j - 1];
        }

        int ans = 0;
        for(int i = 1; i <= n / 2; ++i)
        {
            for(int j = 1; j <= m / 2; ++j)
            {
                int cnt1 = 0, cnt0 = 0;

                if(vt[i][j] == 0) ++cnt0;
                else ++cnt1;
                if(vt[n - i + 1][j] == 0) ++cnt0;
                else ++cnt1;
                if(vt[i][m - j + 1] == 0) ++cnt0;
                else ++cnt1;
                if(vt[n - i + 1][m - j + 1] == 0) ++cnt0;
                else ++cnt1;
                ans += min(cnt0, cnt1);
            }
        }

        int num = 0;
        int num1 = 0;

        if(n % 2)
        {
            for(int i = 1; i <= m / 2; ++i)
            {
                if(vt[n / 2 + 1][i] != vt[n / 2 + 1][m - i + 1])
                {
                    ++ans;
                    ++num;
                }
                else
                {
                    if(vt[n / 2 + 1][i] == 1) ++num1;
                }
            }
        }
        if(m % 2)
        {
            for(int i = 1; i <= n / 2; ++i)
            {
                if(vt[i][m / 2 + 1] != vt[n - i + 1][m / 2 + 1])
                {
                    ++ans;
                    ++num;
                }
                else
                {
                    if(vt[i][m / 2 + 1] == 1) ++num1;
                }
            }
        }

        if(n % 2 == 0 && m % 2 == 0) return ans;
        else
        {
            if(n % 2 == 1 && m % 2 == 1)
            {
                if(vt[n / 2 + 1][m / 2 + 1] == 1) ++ans;
            }
            num1 %= 2;
            if(num < num1) ans += 2;
            return ans;
        }
        return ans;
    }
};
