class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0; i<m/2; i++){
            for(int j=0; j<n/2; j++){
                int a=grid[i][j];
                int b=grid[i][n-1-j];
                int c=grid[m-1-i][j];
                int d=grid[m-1-i][n-1-j];
                int ct=0;
                if(a==1) ct++;
                if(b==1) ct++;
                if(c==1) ct++;
                if(d==1) ct++;
                ans+=min(ct,4-ct);
            }
        }
        int cnt=0;
        if(m%2==1 && n%2==0){
            int ct=0;
            for(int i=0; i<n/2; i++){
                if(grid[m/2][i]==grid[m/2][n-1-i]){
                    if(grid[m/2][i]==1) ct++;
                }
                else cnt++;
            }
            if(ct%2==0){
                ans=ans+cnt;
            }
            else{
                if(cnt==0){
                    ans=ans+cnt+2;
                }
                else{
                    ans=ans+cnt;
                }
            }
        }
        else if(m%2==0 && n%2==1){
            int ct=0;
            for(int i=0; i<m/2; i++){
                if(grid[i][n/2]==grid[m-1-i][n/2]){
                    if(grid[i][n/2]==1) ct++;
                }
                else cnt++;
            }
            if(ct%2==0){
                ans=ans+cnt;
            }
            else{
                if(cnt==0){
                    ans=ans+cnt+2;
                }
                else{
                    ans=ans+cnt;
                }
            }
        }
        else if(m%2==1 && n%2==1){
            int p=0;
            if(grid[m/2][n/2]==1) p++;

            int ct=0;
            for(int i=0; i<n/2; i++){
                if(grid[m/2][i]==grid[m/2][n-1-i]){
                    if(grid[m/2][i]==1) ct++;
                }
                else cnt++;
            }
            for(int i=0; i<m/2; i++){
                if(grid[i][n/2]==grid[m-1-i][n/2]){
                    if(grid[i][n/2]==1) ct++;
                }
                else cnt++;
            }

            if(ct%2==0){
                ans=ans+cnt;
            }
            else{
                if(cnt==0){
                    ans=ans+cnt+2;
                }
                else{
                    ans=ans+cnt;
                }
            }
            ans=ans+p;
        }
        return ans;
    }
};
