class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int count1=0;

        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int item=grid[i][j];
                int count=1;
                if(grid[n-1-i][j] == item)
                count++;

                if(grid[i][m-1-j]==item)
                count++;

                if(grid[n-1-i][m-1-j]==item)
                count++;

                count1 += min(count,4-count);
            }
        }
        int x=0;
        int count2=0;
        if(n%2==1)
        for(int j=0;j<m/2;j++)
        {
            if(grid[n/2][j]!=grid[n/2][m-1-j])
            {
                count2++;
            }else
            {
                if(grid[n/2][j]==1)
                x+=2;
            }
        }
        count1+=count2;

        int y=0;
        int count3 =0;
        if(m%2==1)
        for(int i=0;i<n/2;i++)
        {
            if(grid[i][m/2]!=grid[n-1-i][m/2])
            {
                count3++;
            }
            else
            {
                if(grid[i][m/2]==1)
                y+=2;
            }
        }
        count1 = count1 + count3;
        if((x+y)%4!=0 && (count2+count3)<1)
        {
            count1+=2;
        }

        if(n%2==1 && m%2==1)
        {
            if(grid[n/2][m/2]==1){
                count1+=1;
            }
        }

        return count1;

    }
};
