class Solution {
public:
int minFlips(vector<vector<int>>& grid) {
    int m=grid.size();
    int n=grid[0].size();
    int t=0;
    for(int i=0;i<m/2;++i){
        for(int j=0;j<n/2;++j){
            int t1=0,t2=0;
            if(grid[i][j]){
                ++t1;
            }else{
                ++t2;
            }
            if(grid[m-1-i][j]){
                ++t1;
            }else{
                ++t2;
            }
            if(grid[i][n-1-j]){
                ++t1;
            }else{
                ++t2;
            }
            if(grid[m-1-i][n-1-j]){
                ++t1;
            }else{
                ++t2;
            }
            t+=min(t1,t2);
        }
    }
    if(n%2 && m%2){
        t+=grid[m/2][n/2];
    }
    int t1=0,t2=0;
    if(m%2) {
        for(int i=0;i<n/2;++i){
            if(grid[m/2][i]!=grid[m/2][n-1-i]){
                ++t2;
            }else if(grid[m/2][i] && grid[m/2][n-1-i]){
                ++t1;
            }
        }
    }
    if(n%2) {
        for(int i=0;i<m/2;++i){
            if(grid[i][n/2]!=grid[m-1-i][n/2]){
                ++t2;
            }else if(grid[i][n/2] && grid[m-1-i][n/2]){
                ++t1;
            }
        }
    }
    t+=t2;
    if(t1%2 && t2==0){
        t+=2;
    }

    return t;


}




