class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int a1 = 0;
        for(int i = 0; i < g.size()/2; i++) {
            for(int j = 0; j < g[0].size()/2; j++) {
                int z = 0;
                z += g[i][j] == 0;
                z += g[i][g[i].size() - 1 - j] == 0;
                z += g[g.size() - 1 - i][j] == 0;
                z += g[g.size() - 1 - i][g[i].size() - 1 - j] == 0;
                if(z % 4 == 0) continue;
                else a1 += min(z, 4-z);
            }
        }
        int f = 0;
        int o = 0;
        if(g.size() % 2) {
            for(int j = 0; j < g[0].size()/2; j++) {
                if(g[g.size()/2][j] != g[g.size()/2][g[0].size() - 1 - j]){
                    f = 1;
                    a1++;
                } else if(g[g.size()/2][j] == 1) {
                    o++;
                }
            }
        }
        if(g[0].size() % 2) {
            for(int i = 0; i < g.size()/2; i++) {
                if(g[i][g[0].size()/2] != g[g.size()-1-i][g[0].size()/2]){
                    f = 1;
                    a1++;
                } else if(g[i][g[0].size()/2] == 1) {
                    o++;
                }
            }
        }
        if(o % 2) {
            if(!f) a1 += 2;
        }
        if(g.size() % 2 && g[0].size() % 2 && g[g.size()/2][g[0].size()/2] == 1)a1++;
        return a1;
    }
};
