class Solution {
public:
    int mp[4];
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int res = 0;
        for (int i=0;i<n;i++)
        {
            for (int j=0;j<m;j++)
            {
                if (n%2!=0 && m%2!=0 && i==n/2 && j==m/2)
                {

                    if (grid[i][j]==1) res+=4;
                    grid[i][j] = 0;

                }
                else if (i==n/2 && n%2!=0)
                {
                    int a = grid[i][j], b = grid[i][m-j-1];
                    if (a!=b) mp[2]++;
                    else if (a==1) mp[1]++;
                    else mp[0]++;            
                }
                else if (j==m/2 && m%2!=0)
                {

                    int a = grid[i][j], b = grid[n-i-1][j];
                    if (a!=b) mp[2]++;
                    else if (a==1) mp[1]++;
                    else mp[0]++;        
                }
                else
                {
                    int a = grid[i][j], b = grid[n-i-1][j], c = grid[i][m-j-1], d = grid[n-i-1][m-j-1];
                    res += min(4-(a+b+c+d), a+b+c+d);
                }
            }
        }

        mp[0]/=2;
        mp[1]/=2;
        mp[2]/=2;
        res = res/4;
        if (mp[1]%2!=0)
        {
            if (mp[2]!=0) 
            {
                res++;
                mp[2]-=1;
            }
            else 
            {
                res+=2;
                mp[1]-=1;
            }
        }
        res += mp[2];
        return res;
    }
};
