class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        int twowala=0;
        int onewalijodi=0;
        int zerowalijodi=0;

        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                if(i==n/2 && j==m/2){
                    ans+=grid[i][j];
                }
                else if(i==n/2){
                    int cnt=grid[i][j]+grid[i][m-1-j];
                    if(cnt==1){
                        ans+=min(cnt,2-cnt);
                        zerowalijodi++;
                    }
                    else if(cnt==2){
                        onewalijodi++;
                    }

                }
                else if(j==m/2){
                    int cnt=grid[i][j]+grid[n-1-i][j];
                    if(cnt==1){
                        ans+=min(cnt,2-cnt);
                        zerowalijodi++;
                    }
                    else if(cnt==2){
                        onewalijodi++;
                    }
                }
                else{
                    int cnt=(grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j]);
                    ans+=min(cnt,4-cnt);
                }
            }
        }
        if(onewalijodi%2){
            if(zerowalijodi){
                return ans;
            }
            return ans+2;
        }
        return ans;
    }
};
