class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int res = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int t = 0;
                t += grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];
                if (t == 1 || t == 3) {
                    res += 1;
                } else if (t == 2) {
                    res += 2;
                }
            }
        }
        int x = 0;
        int one = 0;
        if (n % 2 != 0) {
            for (int i = 0; i < m / 2; i++) {
                if (grid[n / 2][i] != grid[n / 2][m - 1 - i]) {
                    x++;
                } else if (grid[n / 2][i] == 1) {
                    one++;
                }
            }
        }
//        if (one % 2 == 1 && x == 0) {
//            x = 2;
//        }
//        res += x;
//        x = 0;
//        one = 0;
        if (m % 2 != 0) {
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] != grid[n - 1 - i][m / 2]) {
                    x++;
                } else if (grid[i][m / 2] == 1) {
                    one++;
                }
            }
        }
        if (one % 2 == 1 && x == 0) {
            x = 2;
        }
        res += x;
        if (n % 2 == 1 && m % 2 == 1 && grid[n / 2][m / 2] == 1) {
            res++;
        }
        return res;
    }
}
