class Solution {
public:
    int minFlips(vector<vector<int>>& arr ) { 
        int ro = arr.size(); int col = arr[0].size();
        int ans = 0 ; 
        vector < int > vc ; 
        for ( int i = 0 ; i <= (ro-1)/2  ; i ++ ){
            for ( int j = 0 ; j <= (col-1)/2 ; j ++ ){
                set < pair < int , int >> st ; 
                st.insert ( {i,j});
                st.insert ( {i,col-1-j});
                st.insert ( {ro-1-i,j});
                st.insert ( {ro-1-i,col-1-j});
                int ones = 0 ; 
                for ( auto it : st ){
                    ones += (arr[it.first][it.second] == 1 );
                }
                if ( st.size() != 4 ){
                    if ( st.size() == 1 ){
                        ans += ones ; 
                    }
                    else { 
                        vc.push_back  ( ones );
                    }
                }
                else {                   
                    ans += min ( ones , 4- ones );
                }
            }
        }
        sort ( vc.begin() , vc.end());
        if ( vc.size() & 1 ){
            ans += vc[0];            
        }

        while ( vc.size() > 1 ){
            int aa = vc.back (); vc.pop_back();
            int bb = vc.back() ; vc.pop_back();
            if ( aa == 2 && bb == 1 ){
                ans += 1 ; 

            }
            else if ( aa == 1 && bb == 1 ){
                ans += 2 ; 

            }
            else if ( aa == 1  && bb == 0 ){
                ans += 1 ; 

            }
            else if ( aa == 2 && bb == 0 ){
                ans += 2 ; 
            }
        }
        return ans ; 
    }
};
