class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int c=0, n=grid.size(), m=grid[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int a = grid[i][j] + grid[i][m-j-1] + grid[n-i-1][j] + grid[n-i-1][m-j-1];
                c += min(a, 4-a);
            }
        }
        int r=0, o=0;
        if(n&1){
            for(int i=n/2, j=0;j<m/2;j++){
                if(grid[i][j] != grid[i][m-j-1]){
                    r++;
                    grid[i][j]=1;
                }
                if(grid[i][j]==1) o+=2;
            }
        }
        if(m&1){
            for(int i=0, j=m/2;i<n/2;i++){
                if(grid[i][j] != grid[n-i-1][j]){
                    r++;
                    grid[i][j]=1;
                }
                if(grid[i][j]==1) o+=2;
            }
        }
        if((n&1) && (m&1) && (grid[n/2][m/2]==1)) c++;
        c+=r;
        if(o%4==2 && r==0){
            c+=2;
        }
        return c;
    }
};
