class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int res = 0;
        for(int i = 0; i < n / 2; i++)
            for(int j = 0; j < m / 2; j++){
                cout << i << ' ' << j << endl;
                int cnt0 = 0, cnt1 = 0;
                grid[i][j] == 1 ? cnt1++ : cnt0++;
                grid[n - i - 1][j] == 1 ? cnt1++ : cnt0++;
                grid[i][m - j - 1] == 1 ? cnt1++ : cnt0++;
                grid[n - i - 1][m - j - 1] == 1 ? cnt1++ : cnt0++;
                res += min(cnt0, cnt1);
            }
        int cnt0 = 0, cnt1 = 0;
        int change = 0;
        int h = n / 2, l = m / 2;
        if(n & 1){
            for(int st = 0, ed = m - 1; st < ed; st++, ed--)
                if(grid[h][st] != grid[h][ed]) change++;
                else grid[h][st] == 1 ? cnt1+=2 : cnt0+=2;
        }
        if(m & 1){
            for(int st = 0, ed = n - 1; st < ed; st++, ed--)
            if(grid[st][l] != grid[ed][l]) change++;
            else grid[st][l] == 1 ? cnt1 += 2 : cnt0 += 2;
        }
        if(n & 1 && m & 1 && grid[h][l]) res++;

        if(cnt1 % 4 == 0) res += change;
        else {
            if(change) res += change;
            else res += 2;
        }
        return res;
    }
};
