class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int n=arr.size();
        int m=arr[0].size();

        int res=0;

        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int cnt_one=0;
                int cnt=0;
                cnt_one+=arr[i][j]==1;
                cnt++;
                if(n-1-i != i)
                {
                    cnt_one+=arr[n-1-i][j]==1;
                    cnt++;
                }
                if(m-1-j != j)
                {
                    cnt_one+=arr[i][m-1-j]==1;
                    cnt++;
                }
                if(n-1-i != i && m-1-j != j)
                {
                    cnt_one+=arr[n-1-i][m-1-j]==1;
                    cnt++;
                }

                res+= min(cnt_one, cnt-cnt_one);
            }
        }

        int ones=0;
        int matches=0;
        int res2=0;

        if(n%2==1)
        {
            for(int j=0;j<m/2;j++)
            {
                if(arr[n/2][j]==arr[n/2][m-1-j])
                {
                    ones+=2*(arr[n/2][j]==1);
                    matches++;
                }
                else
                {
                    res2++;
                }
            }
        }

        if(m%2==1)
        {
            for(int j=0;j<n/2;j++)
            {
                if(arr[j][m/2]==arr[n-1-j][m/2])
                {
                    ones+=2*(arr[j][m/2]==1);
                    matches++;
                }
                else
                {
                    res2++;
                }
            }
        }

        if(ones%4==0)
        {
            res+=res2;
        }
        else
        {
            if(res2>0)
            {
                res+=res2;
            }
            else
            {
                res+=2;
            }
        }

        if(n%2==1 && m%2==1)
        {
            res+=(arr[n/2][m/2]==1);
        }

        return res;
    }
};
