class Solution {
public:
    #define ll long long
    int minFlips(vector<vector<int>>& v) {
        int n = v.size();
        int m = v[0].size();
        vector<vector<int>>z=v;
        int ans = 0,cnt=0;
        ll yy=n/2;
        ll xx=m/2;
        if(n%2==0){
            yy++;
        }
        if(m%2==0){
            xx++;
        }
        for(int i=0;i<yy;i++){
            for(int j=0;j<xx;j++){
                int tl = v[i][j];
                int tr = v[i][m-1-j];
                int bl = v[n-1-i][j];
                int br = v[n-1-i][m-j-1];
                if((bl+br+tr+tl)>=2){
                    v[i][j]=1;
                    v[i][m-1-j]=1;
                    v[n-1-i][j]=1;
                    v[n-1-i][m-1-j]=1;
                    ans+=4-(bl+br+tr+tl);
                }else{
                    cnt-=(bl+br+tr+tl);
                    ans+=(bl+br+tr+tl);
                    v[i][j]=0;
                    v[i][m-1-j]=0;
                    v[n-1-i][j]=0;
                    v[n-1-i][m-1-j]=0;
                }
            }
        }
        // cout<<ans<<endl;
        int c=0,cc=0;
        if(n%2==1){
        for(int i =0;i<m/2;i++){
            if(v[n/2][i]!=v[n/2][m-i-1]){
                cc++;
            }else{
                c+=v[n/2][i]*2;
            }
        }}
        if(m%2==1){
        for(int i =0;i<n/2;i++){
            if(v[n-1-i][m/2]!=v[i][m/2]){
                cc++;
            }else{
                c+=v[n-i-1][m/2]*2;
            }
        }}
        if((cc*2+c)%4==0){
            ans+=cc;
        }else{
            if(cc==0){
                ans+=2;
            }else{
                ans+=cc;
            }
        }
        // cout<<c<<endl;
        int x = v[n/2][m/2];
        if(n%2==1&&m%2==1)
        ans+=v[n/2][m/2];
        return ans;
    }
};
