class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size(), m = grid[0].size();
        int sum = 0, ans = 0;
        for (int i = 0; i < n / 2; i ++ ){
            for (int j = 0; j < m / 2; j ++ ){
                int a = 0, b = 0;
                if (grid[i][j]) a ++ ;
                else b ++ ;
                if (grid[n - i - 1][j]) a ++ ;
                else b ++ ;
                if (grid[i][m - j - 1]) a ++ ;
                else b ++ ;
                if (grid[n - i - 1][m - j - 1]) a ++ ;
                else b ++ ; 
                sum += min(a, b);
            }
        }

        int f1 = 0, f2 = 0;

        if (n % 2 != 0) {
            for (int j = 0; j < m / 2; j ++ ){
                int a = 0, b = 0;
                if (grid[(n + 1) / 2 - 1][j]) a ++ ;
                else b ++ ;
                if (grid[(n + 1) / 2 - 1][m - j - 1]) a ++ ;
                else b ++ ;
                sum += min(a, b);
                if (a == 2){
                    ans += 2;
                }
                if (a == 1) f1 = 1;
            }
        }

        if (m % 2 != 0) {
            for (int i = 0; i < n / 2; i ++ ){
                int a = 0, b = 0;
                if (grid[i][  ((m + 1) / 2) - 1 ]) a ++ ;
                else b ++ ;

                if (grid[n - i - 1][((m + 1) / 2) - 1]) a ++ ;
                else b ++ ;
                sum += min(a, b);
                if (a == 2) {
                    ans += 2;
                }
                if (a == 1) f1 = 1;
            }
        }



        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[(n + 1) / 2 - 1][(m + 1) / 2 - 1]) ans ++ , f2 = 1;
        }

        if (ans % 2 == 1){
            sum ++ ;
            ans -- ;
        }

        if (ans % 4 == 2) {
            if (f1) return sum;
            else return sum + 2;
        }

        return sum;

    }
};
