class Solution {
    public static int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 0;

        for (int i = 0, j = n - 1; i < n / 2; i++, j--) {
            for (int k = 0, l = m - 1; k < m / 2; k++, l--) {
                int a = grid[i][k];
                int b = grid[j][k];
                int c = grid[i][l];
                int d = grid[j][l];
                int cnt1 = 0;
                if (a == 1) ++cnt1;
                if (b == 1) ++cnt1;
                if (c == 1) ++cnt1;
                if (d == 1) ++cnt1;
                if (cnt1 >= 2) {
                    ans += 4 - cnt1;
                } else {
                    ans += cnt1;
                }
            }
        }

        if (n % 2 != 0 && m % 2 != 0) {
            if (grid[n / 2][m / 2] != 0) {
                grid[n / 2][m / 2] = 0;
                ans += 1;
            }
        }

        int cnt1 = 0;
        int cnt2 = 0;
        if (n % 2 != 0) {
            for (int i = 0, j = m - 1; i < m / 2; i++, j--) {
                if (grid[n / 2][i] != grid[n / 2][j]) {
                    ++cnt2;
                } else if (grid[n / 2][i] == 1) {
                    cnt1 += 2;
                }
            }
        }
        if (m % 2 != 0) {
            for (int i = 0, j = n - 1; i < n / 2; i++, j--) {
                if (grid[i][m / 2] != grid[j][m / 2]) {
                    ++cnt2;
                } else if (grid[i][m / 2] == 1) {
                    cnt1 += 2;
                }
            }
        }
        ans += cnt2;
        if (cnt1 % 4 != 0 && cnt2 == 0) ans += 2;
        return ans;
    }
}
