class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        map<pair<int,int>,int> mp; //1,0
        for(int i=0;i<n/2+(n%2);i++)
        {
            int x1=i,x2=n-1-i;
            if(x1==x2)
            {
                for(int j=0;j<m/2+(m%2);j++)
                {
                    int y1=j,y2=m-1-j,cnt=0;
                    if(y1==y2)
                    {
                        cnt=grid[x1][y1];
                        mp[{cnt,1-cnt}]++;
                    }
                    else
                    {
                        cnt=grid[x1][y1]+grid[x1][y2];
                        mp[{cnt,2-cnt}]++;
                    }
                }
            }
            else
            {
                for(int j=0;j<m/2+m%2;j++)
                {
                    int y1=j,y2=m-1-j,cnt=0;
                    if(y1==y2)
                    {
                        cnt=grid[x1][y1]+grid[x2][y1];
                        mp[{cnt,2-cnt}]++;
                    }
                    else
                    {
                        cnt=grid[x1][y1]+grid[x2][y1]+grid[x1][y2]+grid[x2][y2];
                        mp[{cnt,4-cnt}]++;
                    }
                }
            }
        }
        int ans=0,sum=0;
        if(mp[{1,0}]) ans++; 
        ans+=mp[{1,3}]+mp[{3,1}];
        ans+=2*mp[{2,2}];
        sum+=2*mp[{2,0}];
        if(sum%4==0) ans+=mp[{1,1}];
        else
        {
            if(mp[{1,1}]) ans+=mp[{1,1}];
            else ans+=2+mp[{1,1}];
        }
        return ans;
    }
};
