class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();
        int ans = 0;
        for(int i = 0;i<n/2;i++){
            for(int j = 0;j<m/2;j++){
                // cout<<g[i][j]<<" ";
                int temp = 0;
                if(g[i][j] == 1){
                    temp++;
                }
                if(g[n-i-1][j] == 1){
                    temp++;
                }
                if(g[i][m-j-1] == 1){
                    temp++;
                }
                if(g[n-i-1][m-j-1] == 1){
                    temp++;
                }
                ans += min(temp,4-temp);
            }
            // cout<<'\n';
        }
        if(n%2 && m%2){
            if(g[n/2][m/2]){
                ans++;
                g[n/2][m/2] = 0;
            }
            int o = 0;
            int a = 0;
            for(int i = 0;i<n/2;i++){
                if(g[i][m/2] != g[n-i-1][m/2]){
                    o+=2;
                    a++;
                    ans++;
                }
                else if(g[i][m/2] == 1){
                    o+=2;
                }

            }
            for(int i = 0;i<m/2;i++){
                if(g[n/2][i] != g[n/2][m-i-1]){
                    o+=2;
                    a++;
                    ans++;
                }
                else if(g[n/2][i] == 1){
                    o+=2;
                }
            }
            if(o%4){
                if(a == 0){
                    ans+=2;
                }
            }
            return ans;
        }
        if(n%2){
            int o = 0;
            int a = 0;
            for(int i = 0;i<m/2;i++){
                if(g[n/2][i] != g[n/2][m-i-1]){
                    o+=2;
                    a++;
                    ans++;
                }
                else if(g[n/2][i] == 1){
                    o+=2;
                }
            }
            if(o%4){
                if(a == 0){
                    ans+=2;
                }
            }
            return ans;
        }
        if(m%2){
            int o = 0;
            int a = 0;
            for(int i = 0;i<n/2;i++){
                if(g[i][m/2] != g[n-i-1][m/2]){
                    o+=2;
                    a++;
                    ans++;
                }
                else if(g[i][m/2] == 1){
                    o+=2;
                }

            }
            if(o%4){
                if(a == 0){
                    ans+=2;
                }
            }
        }
        return ans;
    }
};
