class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size(),m=grid[0].size();

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=0;
                if(grid[i][j]==1) cnt++;
                if(grid[i][m-1-j]==1) cnt++;
                if(grid[n-1-i][m-1-j]==1) cnt++;
                if(grid[n-1-i][j]==1) cnt++;
                ans+=min(cnt,4-cnt);
            }
        }
        int ones=0,mistakes=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-1-i]) mistakes++;
                else if(grid[n/2][i]==1) ones++;
            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) mistakes++;
                else if(grid[i][m/2]==1) ones++;
            }
        }
        if(ones%2){
            if(mistakes==0){
                ans+=2;
            }
            else{
                ans+=1;
                mistakes--;
            }
        }
        ans+=mistakes;
        if(n%2 && m%2){
           ans+=(grid[n/2][m/2]==1);
        }
        return ans;
    }
};
