class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int res = 0;
        for(int i = 0; i < n / 2; i ++)
        {
            for(int j = 0; j < m / 2; j ++)
            {
                int sum = grid[i][j] + grid[i][m - j - 1] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                res += min(sum, 4 - sum);
            }
        }
        int cnt1 = 0, cnt2 = 0;
        if(n % 2)
        {
            if(m % 2 && grid[n/2][m/2] == 1) res ++;

            for(int i = 0; i < m / 2; i ++) 
            {
                if(grid[n/2][i] != grid[n/2][m - i - 1]) cnt1 ++;
                else if(grid[n/2][i] == 1) cnt2 ++;
            }
        }
        if(m % 2)
        {
            for(int i = 0; i < n / 2; i ++)
            {
                if(grid[i][m/2] != grid[n - i - 1][m/2]) cnt1 ++;
                else if(grid[i][m/2] == 1) cnt2 ++;
            }
        }
        if(cnt2 % 2 == 0 || cnt2 % 2 && cnt1 > 0) res += cnt1;
            else res += cnt1 + 2;
        return res;
    }
};
