class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row = grid.size(), column = grid[0].size();
        int ans = 0;
        for (int i = 0; i*2 < row; i++) {
            if (row%2 && i == row/2) continue;
            for (int j = 0; j*2 < column; j++) {
                 if (column%2 && j == column/2) continue;
                 int count = grid[i][j] + grid[row - 1 - i][j] + grid[i][column - 1 - j] + grid[row - 1 - i][column - 1 - j];
                 ans = ans + min(count, 4 - count);
            }
        }
        int corner = 0;
        int backfill = 0;
        if (row%2 && column%2) {
            ans = ans + grid[row/2][column/2];
            for (int j = 0; j*2 < column; j++) {
                if (grid[row/2][j] != grid[row/2][column - 1 - j]) {
                    ans = ans + 1;
                    backfill += 2;
                } else if (grid[row/2][j] == 1) {
                    corner += 2;
                }
            }
            for (int i = 0; i*2 < row; i++) {
                if (grid[i][column/2] != grid[row - 1 - i][column/2]) {
                    ans = ans + 1;
                    backfill += 2;
                } else if (grid[i][column/2] == 1) {
                    corner += 2;
                }
            }
        } else {
            if (row%2) {
               for (int j = 0; j*2 < column; j++) {
                if (grid[row/2][j] != grid[row/2][column - 1 - j]) {
                    ans = ans + 1;
                    backfill += 2;
                } else if (grid[row/2][j] == 1) {
                    corner += 2;
                }
                }
            }
            if (column%2) {
               for (int i = 0; i*2 < row; i++) {
                if (grid[i][column/2] != grid[row - 1 - i][column/2]) {
                    ans = ans + 1;
                    backfill += 2;
                } else if (grid[i][column/2] == 1) {
                    corner += 2;
                }
            }
            }
        }
        ans = ans + (backfill >= 2? 0:(corner%4));
        return ans;
    }
};
