class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m =grid.size(), n = grid[0].size();
        int ans = 0;
        for (int i = 0; i < m/2; i++) {
            for (int j = 0; j < n/2; j++) {
                int cnt = 
                grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1];
                ans += min(cnt, 4-cnt);
            }
        }
        int sum = 0, tans = 0;
        if (m & 1) {
            for (int j = 0; j < n/2; j++) {
                if (grid[m/2][j] == grid[m/2][n-j-1]) {
                    sum += grid[m/2][j];
                }
                else {
                    tans++;
                }
            }
        }
        if (n & 1) {
            for (int i = 0; i < m/2; i++) {
                if (grid[i][n/2] == grid[m-i-1][n/2]) {
                    sum += grid[i][n/2];
                }
                else {
                    tans++;
                }
            }
        }
        if ((m&1) && (n&1) && grid[m/2][n/2]) {
            ans++;
        }
        if ((sum & 1) && tans == 0) {
            ans += 2;
        }
        ans += tans;
        return ans;
    }
};
