class Solution {
public:
    int minFlips(vector<vector<int>>& mat) {
        int n = mat.size();
        int m = mat[0].size();

        vector<int> cnt(2, 0);
        int ans=0;

        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                cnt[(mat[i][m - 1 - j])]++; 
                cnt[(mat[i][j])]++; 
                cnt[(mat[n - 1 - i][m - 1 - j])]++; 
                cnt[(mat[n - 1 - i][j])]++;

                ans += min(cnt[0], cnt[1]);

                cnt[0]=0;
                cnt[1]=0;
            }
        }


        int eq=0, neq=0;

        if (n % 2 == 1) { 
            for (int i = 0; i < m / 2; i++) { 
                if(mat[n / 2][i] == 1 && mat[n / 2][m - 1 - i]==1) eq+=2;
                if(mat[n / 2][i] + mat[n / 2][m - 1 - i]==1) neq++;

                if (mat[n / 2][i] != mat[n / 2][m - 1 - i]) 
                    ans++; 
            } 
        } 

        if (m % 2 == 1) { 
            for (int i = 0; i < n / 2; i++) { 
                if(mat[i][m / 2] == 1 && mat[n - 1 - i][m / 2]==1) eq+=2;
                if(mat[i][m / 2] + mat[n - 1 - i][m / 2]==1) neq++;

                if (mat[i][m / 2] != mat[n - 1 - i][m / 2]) 
                    ans++; 
            } 
        }


        if(n%2!=0 && m%2!=0){
            if(mat[n/2][m/2]==1) ans++;
        }

        int temp = eq%4;
        if(temp==2){
            if(neq<1) ans+=2;
        }

        return ans;
    }
};
