class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int cnt=0;
                cnt+=grid[i][j]+grid[n-1-i][j]+grid[n-1-i][m-1-j]+grid[i][m-1-j];
                if(cnt<=2)
                {
                    grid[i][j]=0;
                    grid[n-1-i][j]=0;
                    grid[n-1-i][m-1-j]=0;
                    grid[i][m-1-j]=0;
                    ans+=cnt;
                }
                else
                {
                    grid[i][j]=1;
                    grid[n-1-i][j]=1;
                    grid[n-1-i][m-1-j]=1;
                    grid[i][m-1-j]=1;
                    ans+=(4-cnt);
                }
            }
        }
        int av=0;
        int c=0;
        int tot=0;
        if(n%2==1)
        {
            for(int j=0;j<m/2;j++)
            {
                if(grid[n/2][j]!=grid[n/2][m-1-j])
                {
                    av++;
                }
                else
                {
                    if(grid[n/2][j]==1)
                    c+=2;
                }
            }
        }
        if(m%2==1)
        {
            for(int i=0;i<n/2;i++)
            {
                if(grid[i][m/2]!=grid[n-1-i][m/2])
                {
                    av++;
                }
                else
                {
                    if(grid[i][m/2]==1)
                    c+=2;
                }
            }
        }
        if(c%4==2)
        {
            if(av>0)
                ans+=av;
            else
            {
                ans+=av;
                ans+=2;
            }
        }
        else
        {
            ans+=av;
        }
        if(n%2==1 && m%2==1)
        {
            if(grid[n/2][m/2])
            ans++;
        }
        return ans;
    }
};
