class Solution(object):
    def minFlips(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        ans = 0
        # Handle four major region
        for i in range(len(grid) // 2):
            for j in range(len(grid[0]) // 2):
                oneCount = grid[i][j] + grid[i][len(grid[0]) - j - 1] + grid[len(grid) - i - 1][j] + grid[len(grid) - i - 1][len(grid[0]) - j - 1]
                ans += min(oneCount, 4 - oneCount)
        # Handle centre
        if len(grid) % 2 == 1 and len(grid[0]) % 2 == 1:
            ans += grid[(len(grid) // 2)][(len(grid[0]) // 2)]

        # Handle centre line
        oneCount = 0
        forceChangeCount = 0
        if len(grid) % 2 == 1:
            row = grid[(len(grid) // 2)]
            for i in range(len(row) // 2):
                oneCount += row[i] + row[len(row) - 1 - i]
                if row[i] != row[len(row) - 1 - i]:
                    forceChangeCount += 1

        if len(grid[0]) % 2 == 1:
            col = [grid[i][len(grid[0]) // 2] for i in range(len(grid))]
            for i in range(len(col) // 2):
                oneCount += col[i] + col[len(col) - 1 - i]
                if col[i] != col[len(col) - 1 - i]:
                    forceChangeCount += 1

        ans += forceChangeCount

        if forceChangeCount == 0:
            ans += oneCount % 4

        return ans
