class Solution:
    def minFlips(self, a: List[List[int]]) -> int:
        n = len(a)
        m = len(a[0])
        ret = 0
        for i in range(n//2):
            for j in range(m//2):
                s = sum([a[i][j], a[n-i-1][j], a[i][m-j-1], a[n-i-1][m-j-1]])
                ret += min(s, 4-s)

        ct = defaultdict(int)
        if n&1:
            for i in range(m//2):
                x, y = a[n//2][i], a[n//2][m-i-1]
                if x > y: x, y = y, x
                ct[(x, y)] += 1
        if m&1:
            for i in range(n//2):
                x, y = a[i][m//2], a[n-i-1][m//2]
                if x > y: x, y = y, x
                ct[(x, y)] += 1


        if (n&1) and (m&1) and a[n//2][m//2]:
            ret += 1

        def calc(ct):
            if ct[(1, 1)]%2 == 0:
                return ct[(0, 1)]
            if ct[(0, 1)] > 0:
                return ct[(0, 1)]
            return 2

        return calc(ct) + ret
