/**
 * @param {number[][]} grid
 * @return {number}
 */
var minFlips = function(grid) {
    let result = 0;

    for (let r = 0; r < (grid.length >> 1); r++) {
        for (let c = 0; c < (grid[0].length >> 1); c++) {
            const total = grid[r][c] + grid[r][grid[0].length - 1 - c] + grid[grid.length - 1 - r][c] + grid[grid.length - 1 - r][grid[0].length - 1 - c]
            result += Math.min(total, 4 - total);
        }
    }

    let countOne = 0;
    let hasFlip = false;

    if (grid.length % 2 === 1) {
        const r = grid.length >> 1;
        for (let c = 0; c < (grid[0].length >> 1); c++) {
            const total = grid[r][c] + grid[r][grid[0].length - 1 - c]
            if (total === 1) {
                hasFlip = true;
                result++;
            }
            countOne = (countOne + total) % 4;
        }
    }

    if (grid[0].length % 2 === 1) {
        const c = grid[0].length >> 1;
        for (let r = 0; r < (grid.length >> 1); r++) {
            const total = grid[grid.length - 1 - r][c] + grid[r][c]
            if (total === 1) {
                hasFlip = true;
                result++;
            }
            countOne = (countOne + total) % 4;
        }
    }

    if (!hasFlip && countOne % 4 !== 0) {
        result += 2;
    }

    if (grid.length % 2 === 1 && grid[0].length % 2 === 1 && grid[grid.length >> 1][grid[0].length >> 1] === 1) {
        result++;
    }

    return result;
};
